import React, { useState } from "react";
import { useForm } from "react-hook-form"
import { INVALID_TRANSFER_AMOUNT_MESSAGE } from './Constants'
export default function Statement() {

    const { register, handleSubmit, errors } = useForm();
    const onSubmit = data => console.log(data);
    const [appState, setAppState] = useState({
        loading: false,
        searchMode: {
            Last: false,
            Year: false,
            Absolute: false
        }
    });

    return (
        <div>
            <h3>Statement</h3>
            <form onSubmit={handleSubmit(onSubmit)}>
                <table>
                    <tbody>
                        <tr>
                            <td>
                            <input type="radio" name="searchMode" value="Last" id="Last"/>
                            </td>
                        </tr>
                    </tbody>
                </table>


               <input type="radio" name="searchMode" value="Year" id="Year"></input>
               <label for="Year">Year</label><br/>
               <input type="radio" name="searchMode" value="Absolute" id="Absolute"></input>
               <label for="Absolute">From</label><br/>
            </form>
        </div>

    );
}
