import React, { useState } from "react";
import { useForm } from "react-hook-form"
import { INVALID_TRANSFER_AMOUNT_MESSAGE } from './Constants'
export default function Statement() {

    const { register, handleSubmit, errors } = useForm();
    const onSubmit = data => console.log(data);


    const [appState, setAppState] = useState({
        loading: false,
        selectedOption: null
    });

    const handleOptionChange = (event) => {
        setAppState({
            loading: false,
            selectedOption: event.target.value
        })
        console.log(event.target.value)
    };

    return (
        <div>
            <h3>Statement</h3>
            <form onSubmit={handleSubmit(onSubmit)}>
                <table>
                    <tbody>
                        <tr>
                            <td>
                                <input type="radio" name="searchMode" value="Last" id="Last" onChange={handleOptionChange} disabled={appState.selectedOption != 'Last'} />
                            </td>
                            <td>
                                Last
                            </td>
                            <td>
                                <input type="number" placeholder="1..2..3" name="lastNumberOf" />
                            </td>
                            <td>
                                <select name='period'>
                                    <option value="Week">Week</option>
                                    <option value="Month">Month</option>
                                    <option value="Year">Year</option>
                                </select>
                            </td>
                        </tr>
                    </tbody>
                </table>


                <input type="radio" name="searchMode" value="Year" id="Year"></input>
                <label for="Year">Year</label><br />
                <input type="radio" name="searchMode" value="Absolute" id="Absolute"></input>
                <label for="Absolute">From</label><br />
            </form>
        </div>

    );
}
