import React, { useEffect, useState } from "react";
import Loader from './Loader'

export default function EarningStats() {
    const [appState, setAppState] = useState({
        loading: false,
        earningStats: null,
      });


    useEffect(() => {
        setAppState({ loading: true });
        const apiUrl = 'https://run.mocky.io/v3/6ba6add4-2b65-492d-8144-d4575f11b750';
        fetch(apiUrl)
          .then((response) => response.json())
          .then((stats) => {
            sleep(2000)
            setAppState({ loading: false, earningStats: stats });
            console.log(stats)
          });
    }, []);

    if(appState.loading){
        console.log('Loading from api')
        return <Loader/>
    }else{
        console.log('Loading completed')
    }
    return (
        <div>
            <table>
                <tr>
                    <td>Gross Earning</td><td>123</td>
                </tr>
                <tr>
                    <td>Amount in local currency</td><td>1230</td>
                </tr>
                <tr>
                    <td>Current Balance</td><td>500</td>
                </tr>
            </table>
            <form>
                <select name='transferStatsAction'>
                    <option value="Statement">Statement</option>
                    <option value="Transfer">Transfer</option>
                </select>
                <input type="submit" value="Go"></input>
            </form>
            <hr></hr>
        </div>

    );
}
